import React from 'react';

const getCoordinates = (index) => ({
  x: index % 128,
  y: Math.floor(index / 128)
});

const getIndex = (x, y) => y * 128 + x;

const drawRectangle = (start, end) => {
  const points = [];
  
  // Ensure start.x is smaller than end.x and start.y is smaller than end.y
  const topLeftX = Math.min(start.x, end.x);
  const topLeftY = Math.min(start.y, end.y);
  const bottomRightX = Math.max(start.x, end.x);
  const bottomRightY = Math.max(start.y, end.y);
  
  // Draw the four sides of the rectangle
  for (let x = topLeftX; x <= bottomRightX; x++) {
    // Top side
    const topIndex = getIndex(x, topLeftY);
    if (topIndex >= 0 && topIndex < 8192) {
      points.push(topIndex);
    }
    
    // Bottom side
    const bottomIndex = getIndex(x, bottomRightY);
    if (bottomIndex >= 0 && bottomIndex < 8192) {
      points.push(bottomIndex);
    }
  }
  
  for (let y = topLeftY + 1; y < bottomRightY; y++) {
    // Left side
    const leftIndex = getIndex(topLeftX, y);
    if (leftIndex >= 0 && leftIndex < 8192) {
      points.push(leftIndex);
    }
    
    // Right side
    const rightIndex = getIndex(bottomRightX, y);
    if (rightIndex >= 0 && rightIndex < 8192) {
      points.push(rightIndex);
    }
  }
  
  return points;
};

const RectangleMode = ({ isActive, onPixelUpdate, onPreviewUpdate, drawColor = true }) => {
  const updateRectangle = (startPoint, endPoint) => {
    if (!startPoint || !endPoint) return;
    
    const rectanglePoints = drawRectangle(startPoint, endPoint);
    const newPreview = Array(8192).fill(false);
    
    rectanglePoints.forEach(idx => {
      // For white (drawing): set to true to make pixel appear white
      // For black (erasing): set to 'black-preview'
      newPreview[idx] = drawColor ? true : 'black-preview';
    });
    
    return newPreview;
  };

  const handlePreview = (startPoint, currentIndex) => {
    if (!startPoint || typeof currentIndex !== 'number') return;
    
    const currentPoint = getCoordinates(currentIndex);
    const previewState = updateRectangle(startPoint, currentPoint);
    
    // Update the preview state through the normal flow for both colors
    onPreviewUpdate(() => previewState);
  };

  const handleCommit = (startPoint, endIndex) => {
    if (!startPoint || typeof endIndex !== 'number') return;
    
    const endPoint = getCoordinates(endIndex);
    const rectanglePoints = drawRectangle(startPoint, endPoint);
    
    return rectanglePoints;
  };

  return {
    handlePreview,
    handleCommit,
    modeName: 'Rectangle Mode',
    isActive
  };
};

export default RectangleMode;
