import React, { useState } from 'react';
import LineMode from './LineMode';

const Canvas = () => {
  const [pixels, setPixels] = useState(Array(8192).fill(false));
  const [preview, setPreview] = useState(Array(8192).fill(false));
  const [startPoint, setStartPoint] = useState(null);

  const { handlePreview, handleCommit } = LineMode({
    isActive: true,
    onPixelUpdate: setPixels,
    onPreviewUpdate: setPreview
  });

  const handleMouseDown = (index) => {
    setStartPoint(getCoordinates(index));
  };

  const handleMouseMove = (index) => {
    if (startPoint) {
      handlePreview(startPoint, index);
    }
  };

  const handleMouseUp = (index) => {
    handleCommit(startPoint, index);
    setStartPoint(null);
  };

  return (
    <div
      style={{
        display: 'grid',
        gridTemplateColumns: `repeat(128, 5px)`,
        gridTemplateRows: `repeat(64, 5px)`
      }}
    >
      {pixels.map((pixel, index) => {
        const isActive = pixel || preview[index];
        return (
          <div
            key={index}
            style={{
              width: 5,
              height: 5,
              backgroundColor: isActive ? 'black' : 'white',
              border: '1px solid #eee'
            }}
            onMouseDown={() => handleMouseDown(index)}
            onMouseMove={(e) => e.buttons === 1 && handleMouseMove(index)}
            onMouseUp={() => handleMouseUp(index)}
          />
        );
      })}
    </div>
  );
};

export default Canvas;
