import React, { useState, useCallback } from 'react';

const PixelGrid = () => {
  const [pixels, setPixels] = useState(Array(8192).fill(false));
  const [isDrawing, setIsDrawing] = useState(false);

  const handleMouseDown = useCallback((index) => {
    setIsDrawing(true);
    setPixels(prev => {
      const newPixels = [...prev];
      newPixels[index] = true;
      return newPixels;
    });
  }, []);

  const handleMouseEnter = useCallback((index) => {
    if (isDrawing) {
      setPixels(prev => {
        const newPixels = [...prev];
        newPixels[index] = true;
        return newPixels;
      });
    }
  }, [isDrawing]);

  const handleMouseUp = useCallback(() => {
    setIsDrawing(false);
  }, []);

  return (
    <div 
      className="pixel-grid"
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
      onTouchEnd={handleMouseUp}
      onTouchCancel={handleMouseUp}
    >
      {pixels.map((isWhite, index) => (
        <div
          key={index}
          className={`pixel ${isWhite ? 'white' : ''}`}
          onMouseDown={() => handleMouseDown(index)}
          onMouseEnter={() => handleMouseEnter(index)}
          onTouchStart={() => handleMouseDown(index)}
          onTouchMove={(e) => {
            e.preventDefault();
            const touch = e.touches[0];
            const element = document.elementFromPoint(touch.clientX, touch.clientY);
            if (element?.classList.contains('pixel')) {
              const idx = Array.from(element.parentNode.children).indexOf(element);
              handleMouseEnter(idx);
            }
          }}
          style={{ touchAction: 'none' }}
        />
      ))}
    </div>
  );
};

export default PixelGrid;
