import React from 'react';

const getCoordinates = (index) => ({
  x: index % 128,
  y: Math.floor(index / 128)
});

const getIndex = (x, y) => y * 128 + x;

const drawLine = (start, end) => {
  const points = [];
  let x1 = start.x;
  let y1 = start.y;
  let x2 = end.x;
  let y2 = end.y;

  const dx = Math.abs(x2 - x1);
  const dy = Math.abs(y2 - y1);
  const sx = x1 < x2 ? 1 : -1;
  const sy = y1 < y2 ? 1 : -1;
  let err = dx - dy;

  while (true) {
    const index = getIndex(x1, y1);
    if (index >= 0 && index < 8192) {
      points.push(index);
    }

    if (x1 === x2 && y1 === y2) break;
    const e2 = 2 * err;
    if (e2 > -dy) {
      err -= dy;
      x1 += sx;
    }
    if (e2 < dx) {
      err += dx;
      y1 += sy;
    }
  }

  return points;
};

const LineMode = ({ isActive, onPixelUpdate, onPreviewUpdate }) => {
  const handlePreview = (startPoint, currentIndex) => {
    const currentPoint = getCoordinates(currentIndex);
    if (startPoint) {
      onPreviewUpdate(prev => {
        const newPreview = Array(8192).fill(false);
        drawLine(startPoint, currentPoint).forEach(idx => {
          newPreview[idx] = true;
        });
        return newPreview;
      });
    }
  };

  const handleCommit = (startPoint, endIndex) => {
    if (startPoint && typeof endIndex === 'number') {
      const endPoint = getCoordinates(endIndex);
      onPixelUpdate(prev => {
        const newPixels = [...prev];
        drawLine(startPoint, endPoint).forEach(idx => {
          newPixels[idx] = true;
        });
        return newPixels;
      });
      // Clear the preview after committing the line
      onPreviewUpdate(() => Array(8192).fill(false));
    }
  };

  return {
    handlePreview,
    handleCommit,
    modeName: 'Line Mode',
    isActive
  };
};

export default LineMode;
