import React, { useState } from 'react';

const ESP32Connection = ({ pixelData }) => {
  const [ipAddress, setIpAddress] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  const [showSettings, setShowSettings] = useState(false);
  
  // Add a ping function to test connectivity first
  const testConnection = async (ip) => {
    try {
      console.log(`Testing connection to ESP32 at http://${ip}/`);
      
      // Try with a shorter timeout first
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 3000);
      
      try {
        const response = await fetch(`http://${ip}/`, {
          method: 'GET',
          signal: controller.signal,
          mode: 'cors',
          cache: 'no-store',
          headers: {
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache'
          }
        });
        
        clearTimeout(timeoutId);
        
        if (response.ok) {
          console.log("ESP32 connection test successful");
          return true;
        }
        return false;
      } catch (error) {
        clearTimeout(timeoutId);
        console.error("Initial connection test failed:", error);
        
        // Try with a direct image tag as a fallback
        return new Promise((resolve) => {
          console.log("Trying image fallback test...");
          const img = new Image();
          const timeoutId = setTimeout(() => {
            console.log("Image test timed out");
            resolve(false);
          }, 3000);
          
          img.onload = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 reachable via image test");
            resolve(true);
          };
          
          img.onerror = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 not reachable via image test");
            resolve(false);
          };
          
          // Try to load a non-existent image - we expect an error, but this will tell us
          // if the ESP32 is reachable at all
          img.src = `http://${ip}/favicon.ico?nocache=${Date.now()}`;
        });
      }
    } catch (error) {
      console.error("Connection test failed completely:", error);
      return false;
    }
  };
  
  const sendToESP32 = async () => {
    if (!ipAddress) {
      alert('Please enter ESP32 IP address');
      return;
    }
    
    try {
      setConnectionStatus('Testing connection...');
      
      // First check if IP address is valid
      if (!isValidIP(ipAddress)) {
        setConnectionStatus(`Error: "${ipAddress}" is not a valid IP address. Use format: 192.168.1.100`);
        return;
      }
      
      // First test basic connectivity
      const connected = await testConnection(ipAddress);
      if (!connected) {
        setConnectionStatus(`Error: Could not connect to ESP32 at ${ipAddress}.
        
        Troubleshooting steps:
        1. Verify ESP32 is powered on
        2. Confirm IP address is correct (check ESP32 display)
        3. Ensure both devices are on same network
        4. Try opening http://${ipAddress}/ directly in browser
        5. Disable browser extensions/firewall that might block connection`);
        return;
      }
      
      setConnectionStatus('Preparing data...');
      
      // Convert pixel data to a binary format that ESP32 can understand
      // OLED displays typically use 1 bit per pixel (1 for white, 0 for black)
      // For a 128x64 display, that's 1024 bytes (128 * 64 / 8)
      const binaryData = new Uint8Array(1024);
      
      // Fill binary data from pixelData array
      // Each byte represents 8 pixels
      for (let i = 0; i < 8192; i++) {
        const byteIndex = Math.floor(i / 8);
        const bitPosition = i % 8;
        
        if (pixelData[i] === true) { // If pixel is white
          // Set the corresponding bit
          binaryData[byteIndex] |= (1 << (7 - bitPosition));
        }
      }
      
      setConnectionStatus('Sending data...');
      console.log(`Sending ${binaryData.length} bytes to ESP32 at http://${ipAddress}/update`);
      
      // Log first few bytes for debugging
      let firstBytesLog = "First 8 bytes: ";
      for (let i = 0; i < 8; i++) {
        firstBytesLog += binaryData[i].toString(16).padStart(2, '0') + " ";
      }
      console.log(firstBytesLog);
      
      // First, try a regular POST request
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);
      
      let success = false;
      let responseText = "";
      
      try {
        const response = await fetch(`http://${ipAddress}/update`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/octet-stream',
            'Cache-Control': 'no-cache',
            'Pragma': 'no-cache'
          },
          body: binaryData,
          signal: controller.signal,
          mode: 'cors',
          cache: 'no-store'
        });
        
        if (response.ok) {
          responseText = await response.text();
          console.log("ESP32 response:", responseText);
          success = true;
        } else {
          const errorText = await response.text();
          console.error("ESP32 error response:", response.status, errorText);
          responseText = `Error: ${response.status} - ${errorText || 'Unknown error'}`;
        }
      } catch (error) {
        console.error("POST request failed:", error);
        
        // If POST fails, try a simplified GET request as fallback
        try {
          console.log("Trying GET fallback...");
          const getResponse = await fetch(`http://${ipAddress}/update?action=clear`, {
            method: 'GET',
            signal: controller.signal,
            mode: 'cors',
            cache: 'no-store'
          });
          
          if (getResponse.ok) {
            responseText = "Display cleared successfully (fallback method)";
            console.log("Fallback response:", responseText);
            success = true;
          }
        } catch (fallbackError) {
          console.error("Fallback request also failed:", fallbackError);
        }
      }
      
      clearTimeout(timeoutId);
      
      if (success) {
        setConnectionStatus('Sent successfully!');
        setTimeout(() => setConnectionStatus('Connected'), 2000);
      } else {
        setConnectionStatus(responseText || "Unknown error occurred");
      }
    } catch (error) {
      console.error('ESP32 connection error:', error);
      
      if (error.name === 'AbortError') {
        setConnectionStatus('Error: Connection timed out. The ESP32 received the data but may not have responded properly.');
      } else if (error.message.includes('NetworkError')) {
        setConnectionStatus(`Connection failed, but ESP32 may have received the data. 
        If the display updated, this error can be ignored.
        
        Technical details: ${error.message}`);
      } else {
        setConnectionStatus('Error: ' + error.message);
      }
    }
  };
  
  // Helper function to validate IP address format
  const isValidIP = (ip) => {
    const ipPattern = /^((25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
    return ipPattern.test(ip);
  };
  
  return (
    <div className="esp32-connection">
      <button 
        className="esp-button"
        onClick={() => setShowSettings(!showSettings)}
      >
        ESP32 OLED
      </button>
      
      {showSettings && (
        <div className="esp-settings">
          <div>
            <input
              type="text"
              placeholder="ESP32 IP Address"
              value={ipAddress}
              onChange={(e) => setIpAddress(e.target.value)}
            />
            <button onClick={sendToESP32}>Send to OLED</button>
          </div>
          <div className="status" style={{ whiteSpace: 'pre-line' }}>Status: {connectionStatus}</div>
          
          {/* Add manual connection test button */}
          <div style={{ marginTop: '10px' }}>
            <button 
              onClick={async () => {
                setConnectionStatus('Testing direct connection...');
                const result = await testConnection(ipAddress);
                setConnectionStatus(result ? 'ESP32 is reachable!' : 'ESP32 is not reachable');
              }}
              style={{ 
                fontSize: '12px', 
                padding: '4px 8px', 
                backgroundColor: '#555',
                color: 'white',
                border: 'none',
                borderRadius: '4px'
              }}
            >
              Test Connection Only
            </button>
          </div>
          
          <div style={{ marginTop: '10px', fontSize: '12px', color: '#aaa' }}>
            Current IP: {ipAddress || 'Not set'}
          </div>
        </div>
      )}
    </div>
  );
};

export default ESP32Connection;
