import React from 'react';

const getCoordinates = (index) => ({
  x: index % 128,
  y: Math.floor(index / 128)
});

const getIndex = (x, y) => y * 128 + x;

const drawCircle = (center, point) => {
  const points = [];
  // Calculate radius as the distance between center and point
  const radius = Math.floor(Math.sqrt(
    Math.pow(point.x - center.x, 2) + 
    Math.pow(point.y - center.y, 2)
  ));

  // Use the initial click point as the center
  const actualCenter = center;

  let x = radius;
  let y = 0;
  let error = 0;

  while (x >= y) {
    // Add points in all octants using the center point
    const coordinates = [
      [actualCenter.x + x, actualCenter.y + y],
      [actualCenter.x + y, actualCenter.y + x],
      [actualCenter.x - y, actualCenter.y + x],
      [actualCenter.x - x, actualCenter.y + y],
      [actualCenter.x - x, actualCenter.y - y],
      [actualCenter.x - y, actualCenter.y - x],
      [actualCenter.x + y, actualCenter.y - x],
      [actualCenter.x + x, actualCenter.y - y],
    ];

    coordinates.forEach(([px, py]) => {
      if (px >= 0 && px < 128 && py >= 0 && py < 64) {
        const index = getIndex(px, py);
        if (index >= 0 && index < 8192) {
          points.push(index);
        }
      }
    });

    y++;
    error += 1 + 2 * y;
    if (2 * (error - x) + 1 > 0) {
      x--;
      error += 1 - 2 * x;
    }
  }

  return points;
};

const CircleMode = ({ isActive, onPixelUpdate, onPreviewUpdate, drawColor = true }) => {
  // Update circle preview for both white and black
  const updateCircle = (centerPoint, currentPoint) => {
    if (!centerPoint || !currentPoint) return;
    
    const circlePoints = drawCircle(centerPoint, currentPoint);
    
    // Create a new empty preview array - this will clear previous previews
    const newPreview = Array(8192).fill(false);
    
    // For both white and black, update the preview pixels but in different ways
    circlePoints.forEach(idx => {
      // For white (drawing): set to true to make pixel appear white
      // For black (erasing): set to special "black-preview" value
      newPreview[idx] = drawColor ? true : 'black-preview';
    });
    
    return newPreview;
  };

  const handlePreview = (centerPoint, currentIndex) => {
    if (!centerPoint || typeof currentIndex !== 'number') return;
    
    // First ensure any previous DOM manipulations are cleared
    const pixels = document.querySelectorAll('.pixel-grid .pixel');
    pixels.forEach(p => p.style.border = '');
    
    const currentPoint = getCoordinates(currentIndex);
    const previewState = updateCircle(centerPoint, currentPoint);
    
    // Update the preview state through the normal flow for both colors
    onPreviewUpdate(() => previewState);
    
    // Apply direct DOM styling for black preview pixels
    if (!drawColor) {
      // Use a small timeout to ensure React has updated the DOM
      setTimeout(() => {
        const circlePoints = drawCircle(centerPoint, currentPoint);
        circlePoints.forEach(idx => {
          if (pixels[idx]) {
            // Use black background color for black preview
            pixels[idx].style.backgroundColor = '#333';
          }
        });
      }, 0);
    }
  };

  const handleCommit = (centerPoint, endIndex) => {
    if (!centerPoint || typeof endIndex !== 'number') return;
    
    // When committing, clear any custom styles we might have added
    const pixels = document.querySelectorAll('.pixel-grid .pixel');
    pixels.forEach(p => {
      p.style.border = '';
      p.style.backgroundColor = ''; // Reset any background color changes
    });
    
    const endPoint = getCoordinates(endIndex);
    const circlePoints = drawCircle(centerPoint, endPoint);
    
    return circlePoints;
  };

  return {
    handlePreview,
    handleCommit,
    modeName: 'Circle Mode',
    isActive
  };
};

export default CircleMode;
