import React, { useState, useEffect, useRef, useCallback } from 'react';

const ESP32Connection = ({ pixelData }) => {
  const [ipAddress, setIpAddress] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  const [showSettings, setShowSettings] = useState(false);
  const [autoUpdate, setAutoUpdate] = useState(false);
  const previousPixelDataRef = useRef(null);
  const updateIntervalRef = useRef(null);
  const [isConnected, setIsConnected] = useState(false);
  const updateTimeoutRef = useRef(null);
  const debounceTimeRef = useRef(100); // Minimum time between updates
  const lastUpdateTimeRef = useRef(0);
  
  // Memoize these expensive functions
  const arraysEqual = useCallback((a, b) => {
    if (!a || !b || a.length !== b.length) return false;
    
    // Only check a sample of pixels for performance
    // For large arrays, checking every 10th element is usually sufficient
    for (let i = 0; i < a.length; i += 10) {
      if (a[i] !== b[i]) return false;
    }
    
    // If the sample check passes, do a more thorough check
    let changes = 0;
    for (let i = 0; i < a.length; i++) {
      if (a[i] !== b[i]) changes++;
      // Early exit if we find more than 5 changes
      if (changes > 5) return false;
    }
    
    // If we have 5 or fewer changes, consider the arrays equal
    return changes <= 5;
  }, []);
  
  // Convert pixel data to RLE format for efficient transmission
  const convertToRLE = useCallback((pixels) => {
    const rle = [];
    let currentValue = pixels[0] === true;
    let count = 1;
    
    for (let i = 1; i < pixels.length; i++) {
      if (pixels[i] === currentValue) {
        count++;
      } else {
        rle.push({ value: currentValue, count });
        currentValue = pixels[i] === true;
        count = 1;
      }
    }
    
    rle.push({ value: currentValue, count });
    return rle;
  }, []);
  
  // Effect for automatic updates - with performance optimizations
  useEffect(() => {
    if (autoUpdate && ipAddress) {
      // Use a lower frequency for auto-update to improve performance
      updateIntervalRef.current = setInterval(() => {
        const now = Date.now();
        
        // Don't update too frequently
        if (now - lastUpdateTimeRef.current < debounceTimeRef.current) {
          return;
        }
        
        // Use a loose equality check to avoid excessive updates
        if (!previousPixelDataRef.current || 
            !arraysEqual(pixelData, previousPixelDataRef.current)) {
          
          // Clear any pending update
          if (updateTimeoutRef.current) {
            clearTimeout(updateTimeoutRef.current);
          }
          
          // Queue a new update with a short delay 
          // This helps batch several rapid changes together
          updateTimeoutRef.current = setTimeout(() => {
            sendToESP32(false);
            previousPixelDataRef.current = [...pixelData];
            lastUpdateTimeRef.current = Date.now();
            updateTimeoutRef.current = null;
          }, 50);
        }
      }, 200); // Reduced frequency from 100ms to 200ms
      
      return () => {
        if (updateIntervalRef.current) {
          clearInterval(updateIntervalRef.current);
        }
        if (updateTimeoutRef.current) {
          clearTimeout(updateTimeoutRef.current);
        }
      };
    } else if (updateIntervalRef.current) {
      clearInterval(updateIntervalRef.current);
      if (updateTimeoutRef.current) {
        clearTimeout(updateTimeoutRef.current);
      }
    }
  }, [autoUpdate, ipAddress, pixelData, arraysEqual]);
  
  const testConnection = useCallback(async () => {
    if (!ipAddress) return false;
    
    try {
      setConnectionStatus('Testing connection...');
      const response = await fetch(`http://${ipAddress}/ping`, { cache: 'no-store' });
      
      if (response.ok) {
        setConnectionStatus('Connected');
        setIsConnected(true);
        return true;
      } else {
        setConnectionStatus('Connection failed');
        setIsConnected(false);
        return false;
      }
    } catch (error) {
      console.error("Connection test failed:", error);
      setConnectionStatus('Connection failed');
      setIsConnected(false);
      return false;
    }
  }, [ipAddress]);
  
  const sendToESP32 = useCallback(async (updateStatus = true) => {
    if (!ipAddress) {
      if (updateStatus) alert('Please enter ESP32 IP address');
      return;
    }
    
    try {
      if (updateStatus) setConnectionStatus('Sending data...');
      
      // Choose the most efficient format
      let dataToSend;
      const whitePixelCount = pixelData.filter(p => p === true).length;
      
      if (whitePixelCount < 100) {
        // Use index format for small number of white pixels
        const indices = [];
        for (let i = 0; i < pixelData.length; i++) {
          if (pixelData[i] === true) indices.push(i);
        }
        dataToSend = { pixels: indices };
      } else {
        // Use RLE for larger images
        dataToSend = { data: convertToRLE(pixelData) };
      }
      
      const response = await fetch(`http://${ipAddress}/update`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSend),
        cache: 'no-store'
      });
      
      if (response.ok) {
        if (updateStatus) setConnectionStatus('Connected');
        setIsConnected(true);
      } else {
        if (updateStatus) setConnectionStatus('Update failed');
        setIsConnected(false);
      }
    } catch (error) {
      console.error('ESP32 connection error:', error);
      if (updateStatus) setConnectionStatus('Error: ' + error.message);
      setIsConnected(false);
    }
  }, [ipAddress, pixelData, convertToRLE]);
  
  return (
    <div className="esp32-connection">
      <button 
        className="esp-button"
        onClick={() => setShowSettings(!showSettings)}
        style={{ 
          position: 'relative',
          backgroundColor: isConnected ? 
            (autoUpdate ? '#4CAF50' : '#2196F3') : '#555'
        }}
      >
        ESP32 OLED
        {autoUpdate && (
          <span style={{ 
            position: 'absolute',
            top: '-5px',
            right: '-5px',
            backgroundColor: '#ff4444',
            color: 'white',
            borderRadius: '50%',
            width: '10px',
            height: '10px'
          }}></span>
        )}
      </button>
      
      {showSettings && (
        <div className="esp-settings">
          <div>
            <input
              type="text"
              placeholder="ESP32 IP Address"
              value={ipAddress}
              onChange={(e) => setIpAddress(e.target.value)}
            />
            <button 
              onClick={() => sendToESP32(true)}
              disabled={!ipAddress}
            >
              Send to OLED
            </button>
          </div>
          
          <div style={{ 
            marginTop: '10px',
            display: 'flex',
            alignItems: 'center'
          }}>
            <input 
              type="checkbox" 
              id="autoUpdate" 
              checked={autoUpdate}
              onChange={(e) => setAutoUpdate(e.target.checked)}
              style={{ marginRight: '5px' }}
              disabled={!isConnected}
            />
            <label htmlFor="autoUpdate" style={{ color: '#ccc', fontSize: '14px' }}>
              Auto-update (5 FPS)
            </label>
          </div>
          
          <div className="status" style={{ 
            color: isConnected ? '#4CAF50' : '#ff6b6b'
          }}>
            Status: {connectionStatus}
          </div>
          
          <div style={{ marginTop: '10px' }}>
            <button 
              onClick={testConnection}
              style={{ 
                fontSize: '12px', 
                padding: '4px 8px', 
                backgroundColor: '#555',
                color: 'white',
                border: 'none',
                borderRadius: '4px'
              }}
              disabled={!ipAddress}
            >
              Test Connection
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ESP32Connection;
