import React, { useState } from 'react';
import { drawLine } from './utils';

const LineMode = ({ grid, setGrid }) => {
    const [startPoint, setStartPoint] = useState(null);

    const handleCellClick = (x, y) => {
        if (!startPoint) {
            // Set start point of the line
            setStartPoint({ x, y });
        } else {
            // Draw line from start point to end point
            const newGrid = [...grid];
            const setPixel = (x, y) => {
                newGrid[y][x] = 1; // Assuming 1 represents a filled pixel
            };

            drawLine(startPoint.x, startPoint.y, x, y, setPixel);
            setGrid(newGrid);
            setStartPoint(null);
        }
    };

    return (
        <div className="grid-container">
            <div className="grid" style={{ width: 'fit-content' }}>
                {grid.map((row, y) => (
                    <div key={y} className="row">
                        {row.map((cell, x) => (
                            <div
                                key={`${x}-${y}`}
                                className={`cell ${cell ? 'filled' : ''} ${
                                    startPoint?.x === x && startPoint?.y === y ? 'start-point' : ''
                                }`}
                                onClick={() => handleCellClick(x, y)}
                            />
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default LineMode;
