import { getCoordinates } from './utils';

const getIndex = (x, y) => y * 128 + x;

const bucketFill = (startIndex, pixels) => {
  const targetColor = pixels[startIndex];
  if (targetColor === true) return [];
  const width = 128;
  const height = 64;
  const stack = [startIndex];
  const filledPoints = [];
  const visited = new Set();
  while (stack.length > 0) {
    const currentIndex = stack.pop();
    if (visited.has(currentIndex)) continue;
    visited.add(currentIndex);
    if (pixels[currentIndex] !== targetColor) continue;
    filledPoints.push(currentIndex);
    const { x, y } = getCoordinates(currentIndex);
    const neighbors = [
      { dx: 0, dy: -1 },
      { dx: 1, dy: 0 },
      { dx: 0, dy: 1 },
      { dx: -1, dy: 0 }
    ];
    for (const { dx, dy } of neighbors) {
      const newX = x + dx;
      const newY = y + dy;
      if (newX >= 0 && newX < width && newY >= 0 && newY < height) {
        const neighborIndex = getIndex(newX, newY);
        if (!visited.has(neighborIndex) && pixels[neighborIndex] === targetColor) {
          stack.push(neighborIndex);
        }
      }
    }
  }
  return filledPoints;
};

const outlineFill = (startIndex, pixels) => {
  if (pixels[startIndex] !== true) return [];
  const width = 128;
  const height = 64;
  const stack = [startIndex];
  const filledPoints = [];
  const edgePoints = new Set();
  const visited = new Set();
  while (stack.length > 0) {
    const currentIndex = stack.pop();
    if (visited.has(currentIndex)) continue;
    visited.add(currentIndex);
    if (pixels[currentIndex] !== true) continue;
    filledPoints.push(currentIndex);
    const { x, y } = getCoordinates(currentIndex);
    const neighbors = [
      { dx: 0, dy: -1 },
      { dx: 1, dy: 0 },
      { dx: 0, dy: 1 },
      { dx: -1, dy: 0 }
    ];
    let isEdge = false;
    for (const { dx, dy } of neighbors) {
      const newX = x + dx;
      const newY = y + dy;
      if (newX >= 0 && newX < width && newY >= 0 && newY < height) {
        const neighborIndex = getIndex(newX, newY);
        if (pixels[neighborIndex] === true) {
          if (!visited.has(neighborIndex)) {
            stack.push(neighborIndex);
          }
        } else {
          isEdge = true;
        }
      } else {
        isEdge = true;
      }
    }
    if (isEdge) {
      edgePoints.add(currentIndex);
    }
  }
  return filledPoints.filter(idx => !edgePoints.has(idx));
};

const BucketFillMode = ({ isActive, onPixelUpdate, drawColor = true }) => {
  const handleFill = (index, pixels) => {
    if (!isActive) return;
    let pointsToModify;
    if (!drawColor && pixels[index] === true) {
      pointsToModify = outlineFill(index, pixels);
    } else {
      pointsToModify = bucketFill(index, pixels);
    }
    if (pointsToModify.length > 0) {
      onPixelUpdate(prev => {
        const newPixels = [...prev];
        pointsToModify.forEach(idx => {
          newPixels[idx] = drawColor;
        });
        return newPixels;
      });
      return pointsToModify;
    }
    return [];
  };

  return {
    handleFill,
    modeName: 'Bucket Fill',
    isActive
  };
};

export default BucketFillMode;
