import React, { useState, useRef } from 'react';
import FreeformMode from './FreeFormMode';
import LineMode from './LineMode';
import ResetButton from './ResetButton';
import { getCoordinates } from './utils';

const PixelGrid = () => {
  const [pixels, setPixels] = useState(Array(8192).fill(false));
  const [isLineModeActive, setIsLineModeActive] = useState(false);
  const [previewPixels, setPreviewPixels] = useState(Array(8192).fill(false));
  const isDrawingRef = useRef(false);
  const startPointRef = useRef(null);
  const lastPointRef = useRef(null);

  const freeformMode = FreeformMode({
    isActive: !isLineModeActive,
    onPixelUpdate: setPixels
  });

  const lineMode = LineMode({
    isActive: isLineModeActive,
    onPixelUpdate: setPixels,
    onPreviewUpdate: setPreviewPixels
  });

  const handleMouseDown = (index) => {
    isDrawingRef.current = true;
    const point = getCoordinates(index);
    
    if (isLineModeActive) {
      startPointRef.current = point;
    } else {
      lastPointRef.current = freeformMode.handleDraw(null, index);
    }
  };

  const handleMouseEnter = (index) => {
    if (!isDrawingRef.current) return;

    if (isLineModeActive) {
      lineMode.handlePreview(startPointRef.current, index);
      lastPointRef.current = index; // Add this line to track the last point
    } else {
      lastPointRef.current = freeformMode.handleDraw(lastPointRef.current, index);
    }
  };

  const handleMouseUp = () => {
    if (isLineModeActive) {
      lineMode.handleCommit(startPointRef.current, lastPointRef.current);
      setPreviewPixels(Array(8192).fill(false));
    }
    isDrawingRef.current = false;
    startPointRef.current = null;
    lastPointRef.current = null;
  };

  const handleReset = () => {
    setPixels(Array(8192).fill(false));
    setPreviewPixels(Array(8192).fill(false));
    startPointRef.current = null;
    lastPointRef.current = null;
    isDrawingRef.current = false;
  };

  return (
    <div>
      <div style={{ marginBottom: '10px', display: 'flex', gap: '10px' }}>
        <button 
          onClick={() => {
            setIsLineModeActive(false);
            setPreviewPixels(Array(8192).fill(false));
          }}
          style={{ 
            padding: '5px 10px',
            backgroundColor: !isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: !isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {freeformMode.modeName}
        </button>
        <button 
          onClick={() => setIsLineModeActive(true)}
          style={{ 
            padding: '5px 10px',
            backgroundColor: isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {lineMode.modeName}
        </button>
      </div>
      <div 
        className="pixel-grid"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        {pixels.map((isWhite, index) => (
          <div
            key={index}
            className={`pixel ${isWhite || previewPixels[index] ? 'white' : ''}`}
            onMouseDown={() => handleMouseDown(index)}
            onMouseEnter={() => handleMouseEnter(index)}
            style={{ cursor: 'crosshair' }}
          />
        ))}
      </div>
      <ResetButton onReset={handleReset} />
    </div>
  );
};

export default PixelGrid;
