import React, { useState } from 'react';

const ESP32Connection = ({ pixelData }) => {
  const [ipAddress, setIpAddress] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  const [showSettings, setShowSettings] = useState(false);
  
  // Add a ping function to test connectivity first
  const testConnection = async (ip) => {
    try {
      console.log(`Testing connection to ESP32 at http://${ip}/`);
      
      // Try with a shorter timeout first
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 3000);
      
      try {
        const response = await fetch(`http://${ip}/`, {
          method: 'GET',
          signal: controller.signal,
          mode: 'cors',
          cache: 'no-store',
          headers: {
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache'
          }
        });
        
        clearTimeout(timeoutId);
        
        if (response.ok) {
          console.log("ESP32 connection test successful");
          return true;
        }
        return false;
      } catch (error) {
        clearTimeout(timeoutId);
        console.error("Initial connection test failed:", error);
        
        // Try with a direct image tag as a fallback
        return new Promise((resolve) => {
          console.log("Trying image fallback test...");
          const img = new Image();
          const timeoutId = setTimeout(() => {
            console.log("Image test timed out");
            resolve(false);
          }, 3000);
          
          img.onload = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 reachable via image test");
            resolve(true);
          };
          
          img.onerror = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 not reachable via image test");
            resolve(false);
          };
          
          // Try to load a non-existent image - we expect an error, but this will tell us
          // if the ESP32 is reachable at all
          img.src = `http://${ip}/favicon.ico?nocache=${Date.now()}`;
        });
      }
    } catch (error) {
      console.error("Connection test failed completely:", error);
      return false;
    }
  };
  
  const sendToESP32 = async () => {
    if (!ipAddress) {
      alert('Please enter ESP32 IP address');
      return;
    }
    
    try {
      setConnectionStatus('Preparing data...');
      
      // Convert pixel data to a binary format that ESP32 can understand
      const binaryData = new Uint8Array(1024);
      
      // Fill binary data from pixelData array
      for (let i = 0; i < 8192; i++) {
        const byteIndex = Math.floor(i / 8);
        const bitPosition = i % 8;
        
        if (pixelData[i] === true) { // If pixel is white
          binaryData[byteIndex] |= (1 << (7 - bitPosition));
        }
      }
      
      // Create a base64 string from the binary data - this is more HTTP-friendly
      const base64Data = btoa(String.fromCharCode.apply(null, binaryData));
      
      setConnectionStatus('Sending data...');
      
      // Try with a direct FormData approach
      const formData = new FormData();
      formData.append('displayData', base64Data);
      
      const response = await fetch(`http://${ipAddress}/update-base64`, {
        method: 'POST',
        body: formData,
        mode: 'cors',
        cache: 'no-store'
      });
      
      if (response.ok) {
        setConnectionStatus('Sent successfully!');
        setTimeout(() => setConnectionStatus('Connected'), 2000);
      } else {
        setConnectionStatus('Error: ' + (await response.text() || 'Unknown error'));
      }
    } catch (error) {
      console.error('ESP32 connection error:', error);
      setConnectionStatus('Error: ' + error.message);
    }
  };
  
  // Helper function to validate IP address format
  const isValidIP = (ip) => {
    const ipPattern = /^((25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
    return ipPattern.test(ip);
  };
  
  return (
    <div className="esp32-connection">
      <button 
        className="esp-button"
        onClick={() => setShowSettings(!showSettings)}
      >
        ESP32 OLED
      </button>
      
      {showSettings && (
        <div className="esp-settings">
          <div>
            <input
              type="text"
              placeholder="ESP32 IP Address"
              value={ipAddress}
              onChange={(e) => setIpAddress(e.target.value)}
            />
            <button onClick={sendToESP32}>Send to OLED</button>
          </div>
          <div className="status" style={{ whiteSpace: 'pre-line' }}>Status: {connectionStatus}</div>
          
          {/* Add manual connection test button */}
          <div style={{ marginTop: '10px' }}>
            <button 
              onClick={async () => {
                setConnectionStatus('Testing direct connection...');
                const result = await testConnection(ipAddress);
                setConnectionStatus(result ? 'ESP32 is reachable!' : 'ESP32 is not reachable');
              }}
              style={{ 
                fontSize: '12px', 
                padding: '4px 8px', 
                backgroundColor: '#555',
                color: 'white',
                border: 'none',
                borderRadius: '4px'
              }}
            >
              Test Connection Only
            </button>
          </div>
          
          <div style={{ marginTop: '10px', fontSize: '12px', color: '#aaa' }}>
            Current IP: {ipAddress || 'Not set'}
          </div>
        </div>
      )}
    </div>
  );
};

export default ESP32Connection;
