import React, { useState, useRef } from 'react';

const PixelGrid = () => {
  const [pixels, setPixels] = useState(Array(8192).fill(false));
  const [isLineModeActive, setIsLineModeActive] = useState(false);
  const isDrawingRef = useRef(false);
  const lastPointRef = useRef({ x: -1, y: -1 });
  const startPointRef = useRef({ x: -1, y: -1 });

  const getCoordinates = (index) => ({
    x: index % 128,
    y: Math.floor(index / 128)
  });

  const getIndex = (x, y) => y * 128 + x;

  const interpolatePoints = (start, end) => {
    const points = [];
    const dx = Math.abs(end.x - start.x);
    const dy = Math.abs(end.y - start.y);
    const sx = start.x < end.x ? 1 : -1;
    const sy = start.y < end.y ? 1 : -1;
    let err = dx - dy;

    let x = start.x;
    let y = start.y;

    while (true) {
      points.push(getIndex(x, y));
      if (x === end.x && y === end.y) break;
      const e2 = 2 * err;
      if (e2 > -dy) {
        err -= dy;
        x += sx;
      }
      if (e2 < dx) {
        err += dx;
        y += sy;
      }
    }
    return points;
  };

  const handleMouseDown = (index) => {
    isDrawingRef.current = true;
    const point = getCoordinates(index);
    
    if (isLineModeActive) {
      startPointRef.current = point;  // Set start point to clicked position
      lastPointRef.current = point;   // Initialize last point to same position
    } else {
      lastPointRef.current = point;
      setPixels(prev => {
        const newPixels = [...prev];
        newPixels[index] = true;
        return newPixels;
      });
    }
  };

  const handleMouseEnter = (index) => {
    if (!isDrawingRef.current) return;
    
    const point = getCoordinates(index);
    
    if (isLineModeActive) {
      // Just update the last point without drawing preview
      lastPointRef.current = point;
    } else {
      // Freeform drawing remains the same
      const lastPoint = lastPointRef.current;
      if (lastPoint.x !== -1) {
        const points = interpolatePoints(lastPoint, point);
        setPixels(prev => {
          const newPixels = [...prev];
          points.forEach(idx => {
            newPixels[idx] = true;
          });
          return newPixels;
        });
      }
      lastPointRef.current = point;
    }
  };

  const handleMouseUp = () => {
    if (isLineModeActive) {
      // Draw the final line only on mouse up
      const finalPoint = lastPointRef.current;
      if (startPointRef.current.x !== -1 && finalPoint.x !== -1) {
        setPixels(prev => {
          const newPixels = [...prev];
          interpolatePoints(startPointRef.current, finalPoint).forEach(idx => {
            newPixels[idx] = true;
          });
          return newPixels;
        });
      }
      startPointRef.current = { x: -1, y: -1 };
    }
    isDrawingRef.current = false;
    lastPointRef.current = { x: -1, y: -1 };
  };

  return (
    <div>
      <div style={{ marginBottom: '10px', display: 'flex', gap: '10px' }}>
        <button 
          onClick={() => setIsLineModeActive(false)}
          style={{ 
            padding: '5px 10px',
            backgroundColor: !isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: !isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Freeform Mode
        </button>
        <button 
          onClick={() => setIsLineModeActive(true)}
          style={{ 
            padding: '5px 10px',
            backgroundColor: isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Line Mode
        </button>
      </div>
      <div 
        className="pixel-grid"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        {pixels.map((isWhite, index) => (
          <div
            key={index}
            className={`pixel ${isWhite ? 'white' : ''}`}
            onMouseDown={() => handleMouseDown(index)}
            onMouseEnter={() => handleMouseEnter(index)}
            style={{ cursor: 'crosshair' }}
          />
        ))}
      </div>
    </div>
  );
};

export default PixelGrid;
