import React, { useState, useEffect, useRef } from 'react';

const ESP32Connection = ({ pixelData }) => {
  const [ipAddress, setIpAddress] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  const [showSettings, setShowSettings] = useState(false);
  const [autoUpdate, setAutoUpdate] = useState(false);
  const previousPixelDataRef = useRef(null);
  const updateIntervalRef = useRef(null);
  const connectionTimeoutRef = useRef(null);
  const lastUpdateTimeRef = useRef(0);
  const [isConnected, setIsConnected] = useState(false);
  const errorCountRef = useRef(0);
  const maxErrorsBeforeDisablingAuto = 5;
  
  // Effect for automatic updates
  useEffect(() => {
    if (autoUpdate && ipAddress) {
      // Clear any existing interval
      if (updateIntervalRef.current) {
        clearInterval(updateIntervalRef.current);
      }
      
      // Set automatic update at 100ms intervals (10 fps)
      updateIntervalRef.current = setInterval(() => {
        const now = Date.now();
        // Throttle to prevent too many updates - ensure minimum 100ms between updates
        if (now - lastUpdateTimeRef.current >= 100) {
          // Only update if pixels have changed
          if (!previousPixelDataRef.current || 
              !arraysEqual(pixelData, previousPixelDataRef.current)) {
            sendToESP32(false); // Pass false to suppress status updates
            previousPixelDataRef.current = [...pixelData];
            lastUpdateTimeRef.current = now;
          }
        }
      }, 100);
      
      return () => {
        if (updateIntervalRef.current) {
          clearInterval(updateIntervalRef.current);
        }
      };
    } else if (updateIntervalRef.current) {
      clearInterval(updateIntervalRef.current);
    }
  }, [autoUpdate, ipAddress, pixelData]);
  
  // Helper function to compare arrays
  const arraysEqual = (a, b) => {
    if (a.length !== b.length) return false;
    for (let i = 0; i < a.length; i++) {
      if (a[i] !== b[i]) return false;
    }
    return true;
  };
  
  // Convert pixel data to RLE format for more efficient transmission
  const convertToRLE = (pixels) => {
    const rle = [];
    let currentValue = pixels[0] === true;
    let count = 1;
    
    for (let i = 1; i < pixels.length; i++) {
      if (pixels[i] === currentValue) {
        count++;
      } else {
        rle.push({ value: currentValue, count });
        currentValue = pixels[i] === true;
        count = 1;
      }
    }
    
    rle.push({ value: currentValue, count });
    return rle;
  };
  
  // Convert to delta format (only send changes from previous state)
  const convertToDelta = (pixels, prevPixels) => {
    if (!prevPixels) return null;
    
    const changes = [];
    for (let i = 0; i < pixels.length; i++) {
      if (pixels[i] !== prevPixels[i]) {
        changes.push({ index: i, value: pixels[i] === true });
      }
    }
    
    return changes.length < pixels.length * 0.1 ? changes : null; // If too many changes, don't use delta
  };
  
  // Add a ping function to test connectivity first
  const testConnection = async (ip) => {
    try {
      setConnectionStatus('Testing connection...');
      console.log(`Testing connection to ESP32 at http://${ip}/`);
      
      // Try with a shorter timeout first
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 3000);
      
      try {
        const response = await fetch(`http://${ip}/ping`, {
          method: 'GET',
          signal: controller.signal,
          mode: 'cors',
          cache: 'no-store',
          headers: {
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache'
          }
        });
        
        clearTimeout(timeoutId);
        
        if (response.ok) {
          setConnectionStatus('Connected');
          setIsConnected(true);
          errorCountRef.current = 0;
          console.log("ESP32 connection test successful");
          return true;
        }
        
        setConnectionStatus('Connection test failed');
        setIsConnected(false);
        return false;
      } catch (error) {
        clearTimeout(timeoutId);
        console.error("Initial connection test failed:", error);
        
        // Try with a direct image tag as a fallback
        return new Promise((resolve) => {
          console.log("Trying image fallback test...");
          const img = new Image();
          const timeoutId = setTimeout(() => {
            console.log("Image test timed out");
            setConnectionStatus('Connection test failed');
            setIsConnected(false);
            resolve(false);
          }, 3000);
          
          img.onload = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 reachable via image test");
            setConnectionStatus('Connected');
            setIsConnected(true);
            errorCountRef.current = 0;
            resolve(true);
          };
          
          img.onerror = () => {
            clearTimeout(timeoutId);
            console.log("ESP32 not reachable via image test");
            setConnectionStatus('Connection test failed');
            setIsConnected(false);
            resolve(false);
          };
          
          img.src = `http://${ip}/favicon.ico?nocache=${Date.now()}`;
        });
      }
    } catch (error) {
      console.error("Connection test failed completely:", error);
      setConnectionStatus('Connection test failed');
      setIsConnected(false);
      return false;
    }
  };
  
  const sendToESP32 = async (updateStatus = true) => {
    if (!ipAddress) {
      if (updateStatus) alert('Please enter ESP32 IP address');
      return;
    }
    
    try {
      if (updateStatus) setConnectionStatus('Preparing data...');
      
      // Determine the most efficient format to send
      let dataToSend;
      let format;
      
      // Try delta format (only send changes) if we have previous data
      const deltaChanges = convertToDelta(pixelData, previousPixelDataRef.current);
      
      if (deltaChanges && deltaChanges.length > 0) {
        dataToSend = {
          format: 'delta',
          changes: deltaChanges
        };
        format = 'delta';
      } else {
        // If delta isn't appropriate, use RLE format
        const rleData = convertToRLE(pixelData);
        
        // If RLE is significantly smaller than the raw data, use it
        if (rleData.length * 2 < pixelData.filter(p => p === true).length) {
          dataToSend = {
            format: 'rle',
            data: rleData
          };
          format = 'rle';
        } else {
          // Otherwise, fall back to the compact format of just white pixel indices
          const whitePixelIndices = [];
          for (let i = 0; i < 8192; i++) {
            if (pixelData[i] === true) {
              whitePixelIndices.push(i);
            }
          }
          
          dataToSend = {
            format: 'indices',
            pixels: whitePixelIndices
          };
          format = 'indices';
        }
      }
      
      if (updateStatus) setConnectionStatus('Sending data...');
      
      // Set a timeout for the connection
      if (connectionTimeoutRef.current) {
        clearTimeout(connectionTimeoutRef.current);
      }
      
      let timeoutTriggered = false;
      connectionTimeoutRef.current = setTimeout(() => {
        timeoutTriggered = true;
        if (updateStatus) setConnectionStatus('Update timed out, but continuing in background');
      }, 800);
      
      // Try sending with fetch API
      try {
        const response = await fetch(`http://${ipAddress}/update-new`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(dataToSend),
          cache: 'no-store'
        });
        
        if (connectionTimeoutRef.current && !timeoutTriggered) {
          clearTimeout(connectionTimeoutRef.current);
        }
        
        if (response.ok) {
          if (updateStatus) {
            setConnectionStatus(`Connected (${format}) - Auto update ${autoUpdate ? 'ON' : 'OFF'}`);
          }
          setIsConnected(true);
          errorCountRef.current = 0;
          return true;
        } else {
          const errorText = await response.text();
          throw new Error(`Server returned ${response.status}: ${errorText}`);
        }
      } catch (error) {
        console.error('Primary update method failed:', error);
        errorCountRef.current++;
        
        // Fall back to the old method if the new one fails
        try {
          console.log("Falling back to old update method...");
          
          // Always use index format for fallback
          const fallbackData = {
            format: 'indices',
            pixels: pixelData.map((val, idx) => val === true ? idx : -1).filter(idx => idx !== -1)
          };
          
          const fallbackResponse = await fetch(`http://${ipAddress}/update-compact`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(fallbackData),
            cache: 'no-store'
          });
          
          if (fallbackResponse.ok) {
            if (updateStatus) {
              setConnectionStatus(`Connected (fallback) - Auto update ${autoUpdate ? 'ON' : 'OFF'}`);
            }
            setIsConnected(true);
            return true;
          } else {
            throw new Error(`Fallback method failed: ${await fallbackResponse.text()}`);
          }
        } catch (fallbackError) {
          console.error('Fallback update method failed:', fallbackError);
          
          // One more attempt with GET method
          try {
            console.log("Trying final GET fallback...");
            const whitePixelIndices = [];
            for (let i = 0; i < 8192; i++) {
              if (pixelData[i] === true) {
                whitePixelIndices.push(i);
              }
            }
            
            const minimalData = {
              format: 'indices',
              pixels: whitePixelIndices
            };
            
            const encodedData = btoa(JSON.stringify(minimalData));
            const getFallbackUrl = `http://${ipAddress}/update?action=simple&data=${encodedData}`;
            
            const getFallbackResponse = await fetch(getFallbackUrl, {
              method: 'GET',
              cache: 'no-store'
            });
            
            if (getFallbackResponse.ok) {
              if (updateStatus) {
                setConnectionStatus(`Connected (GET fallback) - Auto update ${autoUpdate ? 'ON' : 'OFF'}`);
              }
              setIsConnected(true);
              return true;
            } else {
              throw new Error(`GET fallback failed: ${await getFallbackResponse.text()}`);
            }
          } catch (getFallbackError) {
            if (updateStatus) {
              setConnectionStatus(`Error: Unable to update display after all attempts`);
            }
            setIsConnected(false);
            
            // Disable auto-update if we've had too many consecutive errors
            if (autoUpdate && errorCountRef.current >= maxErrorsBeforeDisablingAuto) {
              console.error('Too many errors, disabling auto-update');
              setAutoUpdate(false);
              setConnectionStatus('Auto-update disabled due to connection errors');
            }
            
            return false;
          }
        }
      }
    } catch (error) {
      console.error('ESP32 connection error:', error);
      if (updateStatus) {
        setConnectionStatus('Error: ' + error.message);
      }
      setIsConnected(false);
      return false;
    }
  };
  
  return (
    <div className="esp32-connection">
      <button 
        className="esp-button"
        onClick={() => setShowSettings(!showSettings)}
        style={{ 
          position: 'relative',
          backgroundColor: isConnected ? 
            (autoUpdate ? '#4CAF50' : '#2196F3') : '#555'
        }}
      >
        ESP32 OLED
        {autoUpdate && (
          <span style={{ 
            position: 'absolute',
            top: '-5px',
            right: '-5px',
            backgroundColor: '#ff4444',
            color: 'white',
            borderRadius: '50%',
            width: '10px',
            height: '10px'
          }}></span>
        )}
      </button>
      
      {showSettings && (
        <div className="esp-settings">
          <div>
            <input
              type="text"
              placeholder="ESP32 IP Address"
              value={ipAddress}
              onChange={(e) => setIpAddress(e.target.value)}
            />
            <button 
              onClick={() => sendToESP32(true)}
              disabled={!ipAddress}
            >
              Send to OLED
            </button>
          </div>
          
          <div style={{ 
            marginTop: '10px',
            display: 'flex',
            alignItems: 'center'
          }}>
            <input 
              type="checkbox" 
              id="autoUpdate" 
              checked={autoUpdate}
              onChange={(e) => {
                setAutoUpdate(e.target.checked);
                if (e.target.checked) {
                  // Reset error counter when manually enabling
                  errorCountRef.current = 0;
                }
              }}
              style={{ marginRight: '5px' }}
              disabled={!isConnected}
            />
            <label htmlFor="autoUpdate" style={{ 
              color: isConnected ? '#ccc' : '#777', 
              fontSize: '14px' 
            }}>
              Auto-update (10 FPS)
            </label>
          </div>
          
          <div className="status" style={{ 
            whiteSpace: 'pre-line',
            color: isConnected ? '#4CAF50' : '#ff6b6b'
          }}>
            Status: {connectionStatus}
          </div>
          
          <div style={{ marginTop: '10px' }}>
            <button 
              onClick={async () => {
                await testConnection(ipAddress);
              }}
              style={{ 
                fontSize: '12px', 
                padding: '4px 8px', 
                backgroundColor: '#555',
                color: 'white',
                border: 'none',
                borderRadius: '4px'
              }}
              disabled={!ipAddress}
            >
              Test Connection
            </button>
          </div>
          
          <div style={{ marginTop: '10px', fontSize: '12px', color: '#aaa' }}>
            Current IP: {ipAddress || 'Not set'}
          </div>
        </div>
      )}
    </div>
  );
};

export default ESP32Connection;
