import React, { useState } from 'react';

const ESP32Connection = ({ pixelData }) => {
  const [ipAddress, setIpAddress] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  const [showSettings, setShowSettings] = useState(false);
  
  // Add a ping function to test connectivity first
  const testConnection = async (ip) => {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      
      const response = await fetch(`http://${ip}/`, {
        method: 'GET',
        signal: controller.signal,
        mode: 'cors',
      });
      
      clearTimeout(timeoutId);
      
      if (response.ok) {
        console.log("ESP32 connection test successful");
        return true;
      }
      return false;
    } catch (error) {
      console.error("ESP32 connection test failed:", error);
      return false;
    }
  };
  
  const sendToESP32 = async () => {
    if (!ipAddress) {
      alert('Please enter ESP32 IP address');
      return;
    }
    
    try {
      setConnectionStatus('Testing connection...');
      
      // First test basic connectivity
      const connected = await testConnection(ipAddress);
      if (!connected) {
        setConnectionStatus('Error: Could not connect to ESP32. Please check IP address and network.');
        return;
      }
      
      setConnectionStatus('Preparing data...');
      
      // Convert pixel data to a binary format that ESP32 can understand
      // OLED displays typically use 1 bit per pixel (1 for white, 0 for black)
      // For a 128x64 display, that's 1024 bytes (128 * 64 / 8)
      const binaryData = new Uint8Array(1024);
      
      // Fill binary data from pixelData array
      // Each byte represents 8 pixels
      for (let i = 0; i < 8192; i++) {
        const byteIndex = Math.floor(i / 8);
        const bitPosition = i % 8;
        
        if (pixelData[i] === true) { // If pixel is white
          // Set the corresponding bit
          binaryData[byteIndex] |= (1 << (7 - bitPosition));
        }
      }
      
      setConnectionStatus('Sending data...');
      console.log(`Sending ${binaryData.length} bytes to ESP32 at http://${ipAddress}/update`);
      
      // Log first few bytes for debugging
      let firstBytesLog = "First 8 bytes: ";
      for (let i = 0; i < 8; i++) {
        firstBytesLog += binaryData[i].toString(16).padStart(2, '0') + " ";
      }
      console.log(firstBytesLog);
      
      // Try with Fetch API using explicit content-type and no-cache
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000); // Increase timeout
      
      const response = await fetch(`http://${ipAddress}/update`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/octet-stream',
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        },
        body: binaryData,
        signal: controller.signal,
        mode: 'cors',
        // Add cache control
        cache: 'no-store'
      });
      
      clearTimeout(timeoutId);
      
      if (response.ok) {
        const responseText = await response.text();
        console.log("ESP32 response:", responseText);
        setConnectionStatus('Sent successfully!');
        setTimeout(() => setConnectionStatus('Connected'), 2000);
      } else {
        const errorText = await response.text();
        console.error("ESP32 error response:", response.status, errorText);
        setConnectionStatus(`Error: ${response.status} - ${errorText || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('ESP32 connection error:', error);
      
      if (error.name === 'AbortError') {
        setConnectionStatus('Error: Connection timed out. The ESP32 received the data but may not have responded properly.');
      } else if (error.message.includes('NetworkError')) {
        setConnectionStatus(`Connection failed, but ESP32 may have received the data. 
        If the display updated, this error can be ignored.
        
        Technical details: ${error.message}`);
      } else {
        setConnectionStatus('Error: ' + error.message);
      }
    }
  };
  
  return (
    <div className="esp32-connection">
      <button 
        className="esp-button"
        onClick={() => setShowSettings(!showSettings)}
      >
        ESP32 OLED
      </button>
      
      {showSettings && (
        <div className="esp-settings">
          <div>
            <input
              type="text"
              placeholder="ESP32 IP Address"
              value={ipAddress}
              onChange={(e) => setIpAddress(e.target.value)}
            />
            <button onClick={sendToESP32}>Send to OLED</button>
          </div>
          <div className="status" style={{ whiteSpace: 'pre-line' }}>Status: {connectionStatus}</div>
          <div style={{ marginTop: '10px', fontSize: '12px', color: '#aaa' }}>
            Current IP: {ipAddress || 'Not set'}
          </div>
        </div>
      )}
    </div>
  );
};

export default ESP32Connection;
