import React, { useState, useRef } from 'react';

const PixelGrid = () => {
  const [pixels, setPixels] = useState(Array(8192).fill(false));
  const [isLineModeActive, setIsLineModeActive] = useState(false);
  const [previewPixels, setPreviewPixels] = useState(Array(8192).fill(false));
  const isDrawingRef = useRef(false);
  const startPointRef = useRef(null);

  const getCoordinates = (index) => ({
    x: index % 128,
    y: Math.floor(index / 128)
  });

  const getIndex = (x, y) => y * 128 + x;

  const drawLine = (start, end) => {
    const points = [];
    let x0 = start.x, y0 = start.y;
    let x1 = end.x, y1 = end.y;

    const dx = Math.abs(x1 - x0);
    const dy = Math.abs(y1 - y0);
    const sx = x0 < x1 ? 1 : -1;
    const sy = y0 < y1 ? 1 : -1;
    
    let err = dx - dy;

    while (true) {
      points.push(getIndex(x0, y0));
      
      if (x0 === x1 && y0 === y1) break;
      
      const e2 = 2 * err;
      if (e2 > -dy) {
        err -= dy;
        x0 += sx;
      }
      if (e2 < dx) {
        err += dx;
        y0 += sy;
      }
    }

    return points;
  };

  const handleMouseDown = (index) => {
    if (isLineModeActive) {
      isDrawingRef.current = true;
      startPointRef.current = getCoordinates(index);
      // Show start point preview
      setPreviewPixels(prev => {
        const newPreview = Array(8192).fill(false);
        newPreview[index] = true;
        return newPreview;
      });
    } else {
      isDrawingRef.current = true;
      setPixels(prev => {
        const newPixels = [...prev];
        newPixels[index] = true;
        return newPixels;
      });
    }
  };

  const handleMouseEnter = (index) => {
    if (!isDrawingRef.current) return;

    if (isLineModeActive) {
      const currentPoint = getCoordinates(index);
      if (startPointRef.current) {
        // Update preview line
        setPreviewPixels(prev => {
          const newPreview = Array(8192).fill(false);
          drawLine(startPointRef.current, currentPoint).forEach(idx => {
            newPreview[idx] = true;
          });
          return newPreview;
        });
      }
    } else {
      // Freeform drawing
      const currentPoint = getCoordinates(index);
      const lastPoint = getCoordinates(pixels.findIndex((pixel, i) => 
        pixel && i === pixels.lastIndexOf(true)
      ));
      
      if (lastPoint) {
        setPixels(prev => {
          const newPixels = [...prev];
          drawLine(lastPoint, currentPoint).forEach(idx => {
            newPixels[idx] = true;
          });
          return newPixels;
        });
      }
    }
  };

  const handleMouseUp = () => {
    if (isLineModeActive && startPointRef.current) {
      // Commit the preview line to the actual pixels
      setPixels(prev => {
        const newPixels = [...prev];
        previewPixels.forEach((isSet, index) => {
          if (isSet) newPixels[index] = true;
        });
        return newPixels;
      });
      // Clear preview and references
      setPreviewPixels(Array(8192).fill(false));
    }
    isDrawingRef.current = false;
    startPointRef.current = null;
  };

  return (
    <div>
      <div style={{ marginBottom: '10px', display: 'flex', gap: '10px' }}>
        <button 
          onClick={() => {
            setIsLineModeActive(false);
            setPreviewPixels(Array(8192).fill(false));
          }}
          style={{ 
            padding: '5px 10px',
            backgroundColor: !isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: !isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Freeform Mode
        </button>
        <button 
          onClick={() => setIsLineModeActive(true)}
          style={{ 
            padding: '5px 10px',
            backgroundColor: isLineModeActive ? '#4CAF50' : '#f0f0f0',
            color: isLineModeActive ? 'white' : 'black',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Line Mode
        </button>
      </div>
      <div 
        className="pixel-grid"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        {pixels.map((isWhite, index) => (
          <div
            key={index}
            className={`pixel ${isWhite || previewPixels[index] ? 'white' : ''}`}
            onMouseDown={() => handleMouseDown(index)}
            onMouseEnter={() => handleMouseEnter(index)}
            style={{ cursor: 'crosshair' }}
          />
        ))}
      </div>
    </div>
  );
};

export default PixelGrid;
